#!/bin/bash

set -e

# env variables
NEXT_PUBLIC_CURRENT_APP_URL="${NEXT_PUBLIC_CURRENT_APP_URL}"
NEXT_PUBLIC_APP_URL="${NEXT_PUBLIC_APP_URL}"
NEXT_PUBLIC_SANITY_PROJECT_ID="${NEXT_PUBLIC_SANITY_PROJECT_ID}"
NEXT_PUBLIC_SANITY_DATASET="${NEXT_PUBLIC_SANITY_DATASET}"

function create_env_file
{
    echo NEXT_PUBLIC_CURRENT_APP_URL=$NEXT_PUBLIC_CURRENT_APP_URL >> .env
    echo NEXT_PUBLIC_APP_URL=$NEXT_PUBLIC_APP_URL >> .env
    echo NEXT_PUBLIC_SANITY_PROJECT_ID=$NEXT_PUBLIC_SANITY_PROJECT_ID >> .env
    echo NEXT_PUBLIC_SANITY_DATASET=$NEXT_PUBLIC_SANITY_DATASET >> .env
}


function run
{
    create_env_file
}

run